and BAPELTTB.period = '${period}'
and ( BAPELTTB.subject_nm like '%'||'${search_name}'||'%' or BAPELTTB.subject_cd like '%'||'${search_name}'||'%' or BAWLECTB.LECTURER_NM like '%'||'${search_name}'||'%' )
