and (BAPELTTB.CAPACITY_NO - (NVL(BAPELTTB.WEB_REGIS_NO,0)) - (NVL(BAPELTTB.REGIS_NO,0))) <= 0
and BAPELTTB.end_yn = 'Y'
and BAPELTTB.end_yn != 'Y'
and BAPELTTB.subject_fg = '${subject_fg}'
and (BAPELTTB.start_ymd between '${search_start}' and '${search_end}')
and BAPELTTB.main_cd = ${main_cd}
and BAPELTTB.sect_cd = ${sect_cd}
and ( 1=2 or (substr(BAPELTTB.day_flag,'1','1')=1 and substr('${day_flag}','1','1')=1) or (substr(BAPELTTB.day_flag,'2','1')=1 and substr('${day_flag}','2','1')=1) or (substr(BAPELTTB.day_flag,'3','1')=1 and substr('${day_flag}','3','1')=1) or (substr(BAPELTTB.day_flag,'4','1')=1 and substr('${day_flag}','4','1')=1) or (substr(BAPELTTB.day_flag,'5','1')=1 and substr('${day_flag}','5','1')=1) or (substr(BAPELTTB.day_flag,'6','1')=1 and substr('${day_flag}','6','1')=1) or (substr(BAPELTTB.day_flag,'7','1')=1 and substr('${day_flag}','7','1')=1) )
